import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';
import nike from './../images/nike.png';
import Bottom from "../Bottom/Bottom";

const Home = () => <div>Home content goes here</div>;
const Catalog = () => <Bottom />;
const Contact = () => <div>Contact content goes here</div>;

const Header = () => {
    const [activeTab, setActiveTab] = useState(0);

    return (
        <Router>
            <hr className="horizontal-line"></hr>
            <div className="header">
                <div className="header-logo">
                    <img src={nike} alt="logo"/>
                </div>
                <ul className="nav-list">
                    <div className="Home-button">
                        <li>
                            <NavLink exact to="/" activeClassName="selected" onClick={() => setActiveTab(0)}>Home</NavLink>
                        </li>
                    </div>

                    <li>
                        <NavLink exact to="/catalog" activeClassName="selected" onClick={() => setActiveTab(1)}>Catalog</NavLink>
                    </li>
                    <li>
                        <NavLink exact to="/contact" activeClassName="selected" onClick={() => setActiveTab(2)}>Contact</NavLink>
                    </li>
                </ul>
                {activeTab === 0 && <Home />}
                {activeTab === 1 && <Catalog />}
                {activeTab === 2 && <Contact />}
            </div>
            <hr className="horizontal-line"></hr>
        </Router>
    );
};

export default Header;
