import React, { useState } from "react";
import {  NavLink } from "react-router-dom";
import nike from '../../images/nike.png';
import CatalogShoes from "../Card/Catalog_cards";
import Card from "../../Home/Card/Card";

const CatalogHeader = () => {
    const [searchTerm, setSearchTerm] = useState('');

    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    const filteredShoes = CatalogShoes.filter(shoe => {
        return shoe.title.toLowerCase().includes(searchTerm.toLowerCase());
    });

    return (
        <div>
            <hr className="horizontal-line"></hr>
            <div className="header">
                <div className="header-logo">
                    <img src={nike} alt="logo"/>
                </div>

                <ul className="nav-list">
                    <div className="Home-button">
                        <li>
                            <NavLink exact to="/" activeClassName="selected">Home</NavLink>
                        </li>
                    </div>

                    <li>
                        <NavLink exact to="/catalog" activeClassName="selected">Catalog</NavLink>
                    </li>
                    <li>
                        <NavLink exact to="/contact" activeClassName="selected">Contact</NavLink>
                    </li>
                </ul>

                    <input className="Search" value={searchTerm} onChange={handleSearchChange} placeholder="Search"></input>

            </div>
            <hr className="horizontal-line"></hr>
            <div className="Card-shoes">
                {filteredShoes.map((shoe) => (
                    <Card key={shoe.id} shoe={shoe} />
                ))}
            </div>
        </div>
    );
};

export default CatalogHeader;
