import React, { useState } from "react";
import shoes1 from '../../images/shoes1.png';
import shoes2 from '../../images/shoes2.png';
import shoes3 from '../../images/shoes3.png';
import shoes4 from '../../images/shoes3.png';
import Card from "./Catalog.Card";
import Select from '../select';

function FilterCatalog() {
    const [selectedOption, setSelectedOption] = useState('all');
    const [selectedOption2, setSelectedOption2] = useState('all');
    const [selectedOption3, setSelectedOption3] = useState('all');

    const handleOptionChange = (event) => {
        setSelectedOption(event.target.value);
    };

    const handleOptionChange2 = (event) => {
        setSelectedOption2(event.target.value);
    };

    const handleOptionChange3 = (event) => {
        setSelectedOption3(event.target.value);
    };

    const shoeData = [
        {
            id: 1,
            image: shoes1,
            title: "Nike Air Ships",
            text: "Червоно-білі кросівки Nike Air Ships, що належали зірці американського баскетболу Майклу Джордану, продали на аукціоні за $1,47 млн. Це",
            text1: "Price:",
            price:  200,
            button : "View more"
        },
        {
            id: 2,
            image: shoes2,
            title: "Nike Cortez",
            text: "американські олімпійці вперше 'показали' кросівки Nike Cortez на Олімпійських іграх в Мюнхені. Тим самим продемонструвавши бренд .",
            text1: "Price:",
            price:  200,
            button : "View more"
        },
        {
            id: 3,
            image: shoes3,
            title: "Air Force",
            text: "Вперше кросівки Air Force 1 випустили 1982 року. Вони призначалися професійних баскетболістів. Модель AF1 - класичний символ стрітстайлу.",
            text1: "Price:",
            price:  200,
            button : "View more"
        },
        {
            id: 4,
            image: shoes4,
            title: "Air Force",
            text: "Вперше кросівки Air Force 1 випустили 1982 року. Вони призначалися професійних баскетболістів. Модель AF1 - класичний символ стрітстайлу.",
            text1: "Price:" ,
            price:  200,
            button : "View more"
        }
    ];

    const filteredShoes = shoeData.filter(shoe => {
        return (selectedOption === 'all' || shoe.price === selectedOption) &&
            (selectedOption2 === 'all' || shoe.title === selectedOption2) &&
            (selectedOption3 === 'all' || shoe.type === selectedOption3);
    });

    return (
        <div className="filter_catalog">
            <nav className="header_catalog__nav1">
                <Select
                    options={[
                        { label: 'Price', value: 'Price' },
                        { label: 'More than 50', value: '76DJHG' },
                        { label: 'More than 10', value: 'GVWUU DHU' },
                        { label: 'More than 2', value: '20-HGFff' },
                    ]}
                    value={selectedOption}
                    onChange={handleOptionChange}
                />
            </nav>
            <nav className="header_catalog__nav2">
                <Select
                    options={[
                        { label: 'Title', value: 'Title' },
                        { label: 'With a number', value: '76DJHG' },
                        { label: 'Without a number', value: 'TRHD-533' },
                        { label: 'With big letters', value: 'GVWUU DHU' },
                    ]}
                    value={selectedOption2}
                    onChange={handleOptionChange2}
                />
            </nav>
            <nav className="header_catalog__nav3">
                <Select
                    options={[
                        { label: 'Type', value: 'Type' },
                        { label: 'For 70 people', value: 'Product 1' },
                        { label: 'For 30 people', value: 'Product 2' },
                        { label: 'For 50 people', value: 'Product 3' },
                    ]}
                    value={selectedOption3}
                    onChange={handleOptionChange3}
                />
            </nav>
            <button className="button_apply">Apply</button>
            <div className="Card-shoes">
                {filteredShoes.map((shoe) => (
                    <Card key={shoe.id} shoe={shoe} />
                ))}
            </div>
        </div>
    );
}

export default FilterCatalog;
