import React from "react";
import {useParams} from "react-router-dom";
import CatalogShoes from '../Card/Catalog_cards'

const Item_catalog = () => {
    const {id} = useParams();
    const selectedItem = CatalogShoes.find((item) => item.id === parseInt(id));
    return (



        <div>
            <h1>{selectedItem.image}</h1>
            <h1>{selectedItem.title}</h1>
            <p>Pharagaph: {selectedItem.text}</p>
            <p>Price: {selectedItem.text1}</p>
            <p>Name: {selectedItem.price}</p>
            <p>Size: {selectedItem.size}</p>

        </div>
    )
}

export default Item_catalog;
