import React, { useState } from "react";

const Card = ({ shoe }) => {
    const [showImage, setShowImage] = useState(false);

    return (
        <div className={`Card-shoes${shoe.id}`}>
            <div className={`Card_photo${shoe.id}`}>
                {showImage && (
                    <div className="Modal">
                        <span className="Close" onClick={() => setShowImage(false)}>X</span>
                        <img className="Modal-content" src={shoe.image} alt={`shoes${shoe.id}`} />
                    </div>
                )}
            </div>
            <h1 className={`Card-title${shoe.id}`}>{shoe.title}</h1>
            <h3 className={`Card-text${shoe.id}`}>{shoe.text}</h3>
            <h2 className={`Card-text-price${shoe.id}`}>{shoe.text1}</h2>
            <h2 className={`Card-price${shoe.id}`}>{shoe.price}</h2>
            <h2 className={`Card-size${shoe.id}`}>{shoe.size}</h2>
            <button className={`Card-button${shoe.id}`} onClick={() => setShowImage(true)}>
                {shoe.button}
            </button>
        </div>
    );
};

export default Card;
